package parsing;

/**
 * 
 */
public class ParseInteger {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String num_str = "123"; // this is string number data
		int num = Integer.parseInt(num_str); // this is the process of converting of string into number 
		
		System.out.println(num_str); // this is string 
		System.out.println(num); // this is number
		System.out.println(num + 4); // this is addition of number
		
		System.out.println(num_str + 4); // this is string concatenation
		
		

	}

}
